import {
  ClassSerializerInterceptor,
  Logger,
  ValidationPipe,
  VersioningType,
} from '@nestjs/common';
import { ConfigService } from '@nestjs/config';
import { NestFactory, Reflector } from '@nestjs/core';
import { DocumentBuilder, SwaggerModule } from '@nestjs/swagger';
import {
  ResolvePromisesInterceptor,
  validationOptions,
} from '@onst/common-lib';
import { useContainer } from 'class-validator';
import 'dotenv/config';
import { AppModule } from './app.module';
import { AllConfigType } from './config';
import { TimeoutInterceptor } from './gateway-timeout.interceptor';
import { GatewayExceptionFilter } from './grpc-exception.filter';
import { LoggingInterceptor } from './logging.interceptor';

async function bootstrap() {
  const logger = new Logger('Bootstrap');
  logger.log('Starting the application...');

  const app = await NestFactory.create(AppModule, {
    logger: ['log', 'error', 'warn', 'debug'],
    cors: true,
  });

  app.enableCors({
    origin: [
      'https://dev-oms-frontend.stock-gpt.ai', // OMS frontend dev
    ],
    methods: ['POST', 'GET', 'PATCH', 'DELETE', 'OPTIONS'],
  });
  useContainer(app.select(AppModule), { fallbackOnErrors: true });
  const configService = app.get(ConfigService<AllConfigType>);

  app.enableShutdownHooks();
  app.setGlobalPrefix(
    configService.getOrThrow('app.apiPrefix', { infer: true }),
    { exclude: ['/'] },
  );
  app.enableVersioning({
    type: VersioningType.URI,
  });
  app.useGlobalPipes(new ValidationPipe(validationOptions));
  app.useGlobalInterceptors(
    // ResolvePromisesInterceptor is used to resolve promises in responses because class-transformer can't do it
    // https://github.com/typestack/class-transformer/issues/549
    new ResolvePromisesInterceptor(),
    new ClassSerializerInterceptor(app.get(Reflector)),
    new TimeoutInterceptor(),
    new LoggingInterceptor(),
  );

  const options = new DocumentBuilder()
    .setTitle('API ')
    .setDescription('API docs')
    .setVersion('1.0')
    .addBearerAuth()
    .build();

  const document = SwaggerModule.createDocument(app, options);
  SwaggerModule.setup('docs', app, document);

  // Enable your global filter here
  app.useGlobalFilters(new GatewayExceptionFilter());

  await app.listen(configService.getOrThrow('app.port', { infer: true }));
  logger.log(`Application is running on: ${await app.getUrl()}`);
}
void bootstrap();

