import { MiddlewareConsumer, Module, NestModule } from '@nestjs/common';
import { ConfigModule } from '@nestjs/config';
import { HealthModule, LoggerModule } from '@onst/common-lib';
import { appConfig, grpcConfig } from './config';
import { CustomMiddlewareModule, AuthModule } from './modules/auth';
import { CredentialsModule } from './modules/credentials';
import { OrderModule } from './modules/order';
import { OrderHistoryModule } from './modules/order-history';
import { PortfolioModule } from './modules/portfolio';
import { SecuritiesModule } from './modules/securities';
import { UserModule } from './modules/user/user.module';

@Module({
  imports: [
    // Global
    LoggerModule,
    HealthModule,

    // Custom Authorization Cognito
    CustomMiddlewareModule,
    AuthModule,

    // Load environment
    ConfigModule.forRoot({
      isGlobal: true,
      load: [appConfig, grpcConfig],
      envFilePath: ['.env'],
      cache: true,
    }),

    // Portfolio Service
    PortfolioModule,

    // Order History Service
    OrderHistoryModule,

    // Order Service
    OrderModule,

    // Securities Service
    SecuritiesModule,
    CredentialsModule,
    UserModule,
  ],
})
export class AppModule implements NestModule {
  configure(consumer: MiddlewareConsumer) {
    // Set default user for testing
    consumer
      .apply((req, res, next) => {
        next();
      })
      .forRoutes('*');
  }
}

