import { Inject, Injectable, Logger, OnModuleInit } from '@nestjs/common';
import { ClientGrpc } from '@nestjs/microservices';
import { firstValueFrom, Observable } from 'rxjs';
import { Metadata } from '@grpc/grpc-js';
import {
  AUTH_SERVICE_NAME,
  AuthServiceClient,
  ConfirmPasswordResetRequest,
  ConfirmPasswordResetResponse,
  GetTokenRequest,
  GetTokenResponse,
  LoginRequest,
  LoginResponse,
  LogoutRequest,
  LogoutResponse,
  RegisterUserRequest,
  RegisterUserResponse,
  RequestPasswordResetRequest,
  RequestPasswordResetResponse,
  VerifyLoginRequest,
  VerifyLoginResponse,
  VerifyRegistrationRequest,
  VerifyRegistrationResponse,
} from '../../protos/auth';
import {
  ConfirmPasswordResetRequestDto,
  ConfirmPasswordResetResponseDto,
  GetTokenResponseDto,
  LoginRequestDto,
  LoginResponseDto,
  LogoutResponseDto,
  RegisterUserRequestDto,
  RegisterUserResponseDto,
  RequestPasswordResetRequestDto,
  RequestPasswordResetResponseDto,
  VerifyLoginRequestDto,
  VerifyLoginResponseDto,
  VerifyRegistrationRequestDto,
  VerifyRegistrationResponseDto,
} from './dto';

@Injectable()
export class AuthService implements OnModuleInit {
  private readonly logger = new Logger(AuthService.name);
  private authService: AuthServiceClient;

  constructor(
    @Inject(AUTH_SERVICE_NAME)
    private client: ClientGrpc,
  ) {}

  onModuleInit() {
    this.authService =
      this.client.getService<AuthServiceClient>(AUTH_SERVICE_NAME);
  }

  private async grpcCall<T>(serviceMethod: Observable<T>): Promise<T> {
    try {
      const response = await firstValueFrom(serviceMethod);
      if (!response) {
        throw new Error(`No response from gRPC service`);
      }
      return response;
    } catch (error) {
      this.logger.error('gRPC call failed', error);
      throw error;
    }
  }

  private generateRequestId(): string {
    return `req_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  async registerUser(
    request: RegisterUserRequestDto,
  ): Promise<RegisterUserResponseDto> {
    const grpcRequest: RegisterUserRequest = {
      requestId: this.generateRequestId(),
      timestamp: new Date().toISOString(),
      data: {
        email: request.email,
        username: request.username,
        password: request.password,
        fullName: request.fullName,
        phoneNumber: request.phoneNumber,
      },
    };

    this.logger.log(`Registering user with email: ${request.email}`);
    const response: RegisterUserResponse = await this.grpcCall(
      this.authService.registerUser(grpcRequest),
    );

    return {
      responseId: response.responseId,
      timestamp: response.timestamp,
      referenceId: response.referenceId,
      code: response.code,
      message: response.message,
      data: response.data
        ? {
            id: response.data.id,
            email: response.data.email,
            username: response.data.username,
            fullName: response.data.fullName,
            phoneNumber: response.data.phoneNumber,
            isActive: response.data.isActive,
            isVerified: response.data.isVerified,
            twoFactorEnabled: response.data.twoFactorEnabled,
            twoFactorType: response.data.twoFactorType,
            createdAt: response.data.createdAt,
            updatedAt: response.data.updatedAt,
            deletedAt: response.data.deletedAt,
            otpRequestId: response.data.otpRequestId,
          }
        : undefined,
    };
  }

  async login(request: LoginRequestDto): Promise<LoginResponseDto> {
    const grpcRequest: LoginRequest = {
      requestId: this.generateRequestId(),
      timestamp: new Date().toISOString(),
      data: {
        email: request.email,
        password: request.password,
      },
    };

    this.logger.log(`User login attempt: ${request.email}`);
    const response: LoginResponse = await this.grpcCall(
      this.authService.login(grpcRequest),
    );

    return {
      responseId: response.responseId,
      timestamp: response.timestamp,
      referenceId: response.referenceId,
      code: response.code,
      message: response.message,
      data: response.data
        ? {
            otpRequestId: response.data.otpRequestId,
          }
        : undefined,
    };
  }

  async verifyLogin(
    request: VerifyLoginRequestDto,
  ): Promise<VerifyLoginResponseDto> {
    const grpcRequest: VerifyLoginRequest = {
      requestId: this.generateRequestId(),
      timestamp: new Date().toISOString(),
      data: {
        email: request.email,
        otpRequestId: request.otpRequestId,
        otpCode: request.otpCode,
      },
    };

    this.logger.log(`Verifying login for email: ${request.email}`);
    const response: VerifyLoginResponse = await this.grpcCall(
      this.authService.verifyLogin(grpcRequest),
    );

    return {
      responseId: response.responseId,
      timestamp: response.timestamp,
      referenceId: response.referenceId,
      code: response.code,
      message: response.message,
      data: response.data
        ? {
            token: response.data.token,
            refreshToken: response.data.refreshToken,
          }
        : undefined,
    };
  }

  async getToken(refreshToken: string): Promise<GetTokenResponseDto> {
    const grpcRequest: GetTokenRequest = {
      requestId: this.generateRequestId(),
      timestamp: new Date().toISOString(),
      data: {
        refreshToken: refreshToken,
      },
    };

    this.logger.log(`Getting token with refresh token`);

    const metadata = new Metadata();
    metadata.add('authorization', `Bearer ${refreshToken}`);

    // Get the raw gRPC client
    const rawClient = this.client.getService<any>(AUTH_SERVICE_NAME); // Use 'any' for now

    const response: GetTokenResponse = await firstValueFrom(
      rawClient.getToken(grpcRequest, metadata), // Pass metadata directly
    );

    return {
      responseId: response.responseId,
      timestamp: response.timestamp,
      referenceId: response.referenceId,
      code: response.code,
      message: response.message,
      data: response.data
        ? {
            token: response.data.token,
          }
        : undefined,
    };
  }

  async logout(): Promise<LogoutResponseDto> {
    const grpcRequest: LogoutRequest = {
      requestId: this.generateRequestId(),
      timestamp: new Date().toISOString(),
    };

    this.logger.log('User logout');
    const response: LogoutResponse = await this.grpcCall(
      this.authService.logout(grpcRequest),
    );

    return {
      responseId: response.responseId,
      timestamp: response.timestamp,
      referenceId: response.referenceId,
      code: response.code,
      message: response.message,
      data: response.data
        ? {
            success: response.data.success,
          }
        : undefined,
    };
  }

  async verifyRegistration(
    request: VerifyRegistrationRequestDto,
  ): Promise<VerifyRegistrationResponseDto> {
    const grpcRequest: VerifyRegistrationRequest = {
      requestId: this.generateRequestId(),
      timestamp: new Date().toISOString(),
      data: {
        otpRequestId: request.otpRequestId,
        otpCode: request.otpCode,
      },
    };

    this.logger.log(
      `Verifying registration with OTP request ID: ${request.otpRequestId}`,
    );
    const response: VerifyRegistrationResponse = await this.grpcCall(
      this.authService.verifyRegistration(grpcRequest),
    );

    return {
      responseId: response.responseId,
      timestamp: response.timestamp,
      referenceId: response.referenceId,
      code: response.code,
      message: response.message,
      data: response.data
        ? {
            id: response.data.id,
            email: response.data.email,
            username: response.data.username,
            fullName: response.data.fullName,
            phoneNumber: response.data.phoneNumber,
            isActive: response.data.isActive,
            isVerified: response.data.isVerified,
            twoFactorEnabled: response.data.twoFactorEnabled,
            twoFactorType: response.data.twoFactorType,
            createdAt: response.data.createdAt,
            updatedAt: response.data.updatedAt,
            deletedAt: response.data.deletedAt,
          }
        : undefined,
    };
  }

  async requestPasswordReset(
    request: RequestPasswordResetRequestDto,
  ): Promise<RequestPasswordResetResponseDto> {
    const grpcRequest: RequestPasswordResetRequest = {
      requestId: this.generateRequestId(),
      timestamp: new Date().toISOString(),
      data: {
        email: request.email,
      },
    };

    this.logger.log(`Password reset requested for email: ${request.email}`);
    const response: RequestPasswordResetResponse = await this.grpcCall(
      this.authService.requestPasswordReset(grpcRequest),
    );

    return {
      responseId: response.responseId,
      timestamp: response.timestamp,
      referenceId: response.referenceId,
      code: response.code,
      message: response.message,
      data: response.data
        ? {
            otpRequestId: response.data.otpRequestId,
            email: response.data.email,
            expiresInSeconds: response.data.expiresInSeconds,
          }
        : undefined,
    };
  }

  async confirmPasswordReset(
    request: ConfirmPasswordResetRequestDto,
  ): Promise<ConfirmPasswordResetResponseDto> {
    const grpcRequest: ConfirmPasswordResetRequest = {
      requestId: this.generateRequestId(),
      timestamp: new Date().toISOString(),
      data: {
        otpRequestId: request.otpRequestId,
        otpCode: request.otpCode,
        newPassword: request.newPassword,
      },
    };

    this.logger.log(
      `Password reset confirmation for OTP request ID: ${request.otpRequestId}`,
    );
    const response: ConfirmPasswordResetResponse = await this.grpcCall(
      this.authService.confirmPasswordReset(grpcRequest),
    );

    return {
      responseId: response.responseId,
      timestamp: response.timestamp,
      referenceId: response.referenceId,
      code: response.code,
      message: response.message,
      data: response.data
        ? {
            success: response.data.success,
          }
        : undefined,
    };
  }
}

