            status: this.mapOrderStatusToString(response.order.status),
            message: response.order.rejectReason,
            requestId: response.order.requestId,
            symbol: response.order.symbol,
            market: response.order.marketId,
            buySell: this.mapOrderSideToString(response.order.side),
            orderType: this.mapOrderTypeToString(response.order.orderType),
            price: response.order.price,
            quantity: response.order.quantity,
            timestamp: this.timestampToISOString(response.order.createdAt),
          }
        : undefined,
    };
  }

  private mapOrderStatusToString(status: OrderStatus): string {
    return OrderStatus[status];
  }

  private mapOrderSideToString(side: OrderSide): string {
    return OrderSide[side];
  }

  private mapOrderTypeToString(type: OrderType): string {
    return OrderType[type];
  }

  private mapOrderTypeToProto(orderType: string): OrderType {
    switch (orderType) {
      case 'LO':
        return OrderType.ORDER_TYPE_LO;
      case 'ATO':
        return OrderType.ORDER_TYPE_ATO;
      case 'ATC':
        return OrderType.ORDER_TYPE_ATC;
      case 'MP':
        return OrderType.ORDER_TYPE_MP;
      case 'MTL':
        return OrderType.ORDER_TYPE_MTL;
      case 'MOK':
        return OrderType.ORDER_TYPE_MOK;
      case 'MAK':
        return OrderType.ORDER_TYPE_MAK;
      case 'PLO':
        return OrderType.ORDER_TYPE_PLO;
      default:
        return OrderType.ORDER_TYPE_UNSPECIFIED;
    }
  }

  private timestampToISOString(timestamp: Timestamp | undefined): string {
    if (!timestamp) {
      return '';
    }
    const date = new Date(
      timestamp.seconds * 1000 + timestamp.nanos / 1_000_000,
    );
    return date.toISOString();
  }

  async getOrderHistory(
    data: GetOrderHistoryRequestDto,
    token: string,
  ): Promise<GetOrderHistoryResponseDto> {
    const request: GetOrderHistoryRequest = {
      requestId: data.requestId,
      timestamp: data.timestamp,
      data: {
        brokerId: data.data.brokerId,
        accountNumber: data.data.accountNumber,
        startDate: data.data.startDate,
        endDate: data.data.endDate,
      },
    };

    const metadata = new Metadata();
    metadata.add('authorization', token);

    const response: GetOrderHistoryResponse = await this.grpcCall(
      (this.orderService as any).getOrderHistory(request, metadata),
    );

    return {
      responseId: response.responseId,
      timestamp: response.timestamp,
      referenceId: response.referenceId,
      code: response.code,
      message: response.message,
      data: response.data.map((item) => ({
        id: item.id || undefined,
        orderId: item.orderId || undefined,
        buySell: item.buySell || undefined,
        price: item.price || undefined,
        quantity: item.quantity || undefined,
        filledQty: item.filledQty || undefined,
        orderStatus: item.orderStatus || undefined,
        marketId: item.marketId || undefined,
        inputTime: item.inputTime || undefined,
        modifiedTime: item.modifiedTime || undefined,
        symbol: item.symbol || undefined,
        orderType: item.orderType || undefined,
        cancelQty: item.cancelQty || undefined,
        avgPrice: item.avgPrice || undefined,
        isForceSell: item.isForceSell || undefined,
        isShortSell: item.isShortSell || undefined,
        rejectReason: item.rejectReason || undefined,
      })),
    };
  }
