import { ChannelOptions, status as GRPCStatus } from '@grpc/grpc-js';
import {
  HttpException,
  HttpStatus,
  ServiceUnavailableException,
} from '@nestjs/common';
import { firstValueFrom, Observable } from 'rxjs';
import { ErrorHandler, IGrpcError } from './grpc-exception.filter';
import { LoggingInterceptor } from './logging.interceptor';

export const GRPC_CHANNEL_OPTIONS: ChannelOptions = {
  'grpc.lb_policy_name': 'round_robin',
  'grpc.service_config': JSON.stringify({
    loadBalancingConfig: [{ round_robin: {} }],
  }),
};

export async function grpcCallUtil<T>(
  func: Observable<T>,
  maxRetries = 3,
  retryDelay = 1000, // 1 seconds
): Promise<T> {
  const startTime = Date.now();
  const logger = new LoggingInterceptor();

  const RETIRABLE_STATUS_CODES = [
    GRPCStatus.UNAVAILABLE,
    GRPCStatus.DEADLINE_EXCEEDED,
    GRPCStatus.INTERNAL,
    GRPCStatus.UNKNOWN,
    GRPCStatus.CANCELLED,
  ];

  for (let attempt = 1; attempt <= maxRetries; attempt++) {
    try {
      const result = await firstValueFrom(func);
      return result;
    } catch (error) {
      const duration = Date.now() - startTime;
      const statusCode = error?.code || GRPCStatus.INTERNAL;
      const errorMessage = error?.message || 'Fail to call GRPC service';

      logger.logWarning({
        method: 'GRPC_CALL',
        duration,
        status: statusCode || 503,
        code: HttpStatus[503] || 'SERVICE_UNAVAILABLE',
        message: `Attempt ${attempt}/${maxRetries}: ${errorMessage}`,
      });

      const shouldRetry = RETIRABLE_STATUS_CODES.includes(statusCode);
      const hasMoreAttempts = attempt < maxRetries;

      if (!shouldRetry) {
        const exception = ErrorHandler.handleGrpcError(error as IGrpcError);
        throw new HttpException(
          {
            statusCode: exception.statusCode,
            message: exception.message,
          },
          exception.statusCode,
        );
      }

      if (!hasMoreAttempts) {
        throw new ServiceUnavailableException(
          'GRPC service unavailable after max retries',
        );
      }

      await new Promise((resolve) => setTimeout(resolve, retryDelay));
    }
  }

  throw new Error('Unreachable');
}
