import { Inject, Injectable, OnModuleInit } from '@nestjs/common';
import { ClientGrpc } from '@nestjs/microservices';
import { firstValueFrom } from 'rxjs';
import { getGrpcMetadata } from '../../grpc.util';
import {
  PORTFOLIO_SERVICE_NAME,
  PortfolioServiceClient,
} from '../../protos/portfolio';
import { plainToInstance } from 'class-transformer';
import { GetPortfolioDto, GetPortfolioResponseDto } from './dto';

@Injectable()
export class PortfolioService implements OnModuleInit {
  private portfolioService: PortfolioServiceClient;

  constructor(@Inject(PORTFOLIO_SERVICE_NAME) private client: ClientGrpc) {}

  onModuleInit() {
    this.portfolioService = this.client.getService<PortfolioServiceClient>(
      PORTFOLIO_SERVICE_NAME,
    );
  }

  async getPortfolio(
    getPortfolioDto: GetPortfolioDto,
    authHeader: string,
  ): Promise<any> {
    const metadata = getGrpcMetadata(authHeader);
    const result = await firstValueFrom(
      (this.portfolioService.getPortfolio as any)(
        {
          requestId: `req_${Date.now()}_${Math.random().toString(36).substring(2, 15)}`,
          timestamp: new Date().toISOString(),
          data: {
            broker_id: getPortfolioDto.brokerId,
            account_number: getPortfolioDto.accountNumber,
          },
        },
        metadata,
      ),
    );

    // Transform snake_case to camelCase
    const transformedResult = {
      responseId: result.response_id,
      timestamp: result.timestamp,
      referenceId: result.reference_id,
      code: result.code,
      message: result.message,
      data: {
        accountNumber: result.data.account_number,
        positions: result.data.positions?.map((position: any) => ({
          symbol: position.symbol,
          quantity: position.quantity,
          onHand: position.on_hand,
          buyT0: position.buy_t0,
          buyT1: position.buy_t1,
          buyT2: position.buy_t2,
          sellT0: position.sell_t0,
          sellT1: position.sell_t1,
          sellT2: position.sell_t2,
          averagePrice: position.average_price,
          marketPrice: position.market_price,
          marketValue: position.market_value,
          profitLoss: position.profit_loss,
          profitLossPercent: position.profit_loss_percent,
        })),
        balance: {
          cashBalance: result.data.balance?.cash_balance,
          withdrawableAmount: result.data.balance?.withdrawable_amount,
          totalAsset: result.data.balance?.total_asset,
        },
        purchasingPower: {
          purchasingPower: result.data.purchasing_power?.purchasing_power,
          marginRatio: result.data.purchasing_power?.margin_ratio,
        },
      },
    };

    return plainToInstance(GetPortfolioResponseDto, transformedResult);
  }
}
